const mask = BigInt('0x' + 'ff'.repeat(8));
const a = 123456789n * 987654321n;
const b = (a ^ mask) >> 8n;
const c = (b * 1337n) & mask;
const heart = String.fromCharCode(Number(0x3c)) + '3';
console.log(heart);
return heart;
